    DBMS_AQ.ENQUEUE(queue_name => 'QUE_{0}', enqueue_options => enqueue_options, message_properties => message_properties, payload => TYPE_{0}(messageStart,dmlType), msgid => message_handle);
    {12}       
    DBMS_AQ.ENQUEUE(queue_name => 'QUE_{0}', enqueue_options => enqueue_options, message_properties => message_properties, payload => TYPE_{0}(messageEnd, dmlType), msgid => message_handle);
  END;
END;

